    IMessageStructure,
    Initializable,
    ContextUpgradeable
{
    struct SwapData {
        uint256 nonce;
        uint256 exchange;
        string swapType;
    }

    address public bscBridgeAgentImpl;
    address public proxyAdmin;
    address public owner;
