pragma solidity 0.8.11;

import "../interfaces/IMessageStructure.sol";

/* Signature Verification

How to Sign and Verify
# Signing
1. Create message to sign (according to EIP-712)
2. Hash the message
3. Sign the hash (off chain, keep your private key secret)

# Verify
1. Recreate hash from the original message
2. Recover signer from signature and hash
3. Compare recovered signer to claimed signer
*/

library VerifySignature {
    bytes32 private constant EIP712_DOMAIN_TYPEHASH =
        keccak256(
            "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
        );

    bytes32 private constant MESSAGE_TYPEHASH =
        keccak256(
            "Bridge(string swapType,address account,string base,string quote,uint256 fee,uint256 amount,uint256 exchange,uint256 deadline,uint256 nonce)"
        );

    /* 1. Unlock MetaMask account
    ethereum.enable()
    */

    /* 2. Get message hash to sign
    getMessageHash(
        1000000000000000000000, // amount
        30000000000000000, // swap fee
        102, // nonce
        1646225186, // deadline
        0xE1B7906410dF6d52598F8a500EC2F07d6D936b1D, // user
        97, // chain id
        0xaea5a85255406f5e22f56b4c889f24b4e10562f6, // verifying contract
        "TotemFi", // name
        "2" // version
    )

    hash = "0xd4dd7fda400cfe0c0e0f83b0fbdc4d77507dab005c4ccacc4025306664a57ef0"
    */
    function getMessageHash(
        IMessageStructure.Message memory _message,
        IMessageStructure.DomainSeparator memory _domain
    ) internal pure returns (bytes32) {
        return
            keccak256(
                abi.encodePacked(
                    getDomainSeparatorHash(_domain),
                    keccak256(
                        abi.encodePacked(
                            MESSAGE_TYPEHASH,
                            keccak256(abi.encode(_message.swapType)),
                            _message.account,
                            keccak256(abi.encode(_message.base)),
                            keccak256(abi.encode(_message.quote)),
                            _message.fee,
                            _message.amount,
                            _message.exchange,
                            _message.deadline,
                            _message.nonce
                        )
                    )
                )
            );
    }

    function getDomainSeparatorHash(
        IMessageStructure.DomainSeparator memory _domain
    ) internal pure returns (bytes32) {
        return
            keccak256(
                abi.encodePacked(
                    EIP712_DOMAIN_TYPEHASH,
                    keccak256(abi.encode(_domain.name)),
                    keccak256(abi.encode(_domain.version)),
                    _domain.chainId,
                    _domain.verifyingContract
                )
            );
    }

    /* 3. Sign message hash
    # using browser
    account = "copy paste account of signer here"
    ethereum.request({ method: "personal_sign", params: [account, hash]}).then(console.log)

    # using web3
    web3.personal.sign(hash, web3.eth.defaultAccount, console.log)

    Signature will be different for different accounts
    "0x94061fd43783e7f9fc4cab8a46d952602695a3c3dad1ac18ea37fe24b70626177d307eed6e9e6cceb21374233512ba08b63a7819d1d76d30e661e658674191351b"

    getEthSignedMessageHash is an internal function used by ecrecover() and is not used from outside

    */
    function getEthSignedMessageHash(bytes32 _messageHash)
        internal
        pure
        returns (bytes32)
    {
        /*
        Signature is produced by signing a keccak256 hash with the following format:
        "\x19Ethereum Signed Message\n" + len(msg) + msg
        */
        return
            keccak256(
                abi.encodePacked(
                    "\x19Ethereum Signed Message:\n32",
                    _messageHash
                )
            );
    }

    /* 4. Verify signature
    signer = 0x1E8a9D0Bd8C19bB27CBb38A997b16B8373578E8a
    signature = 
        "0x94061fd43783e7f9fc4cab8a46d952602695a3c3dad1ac18ea37fe24b70626177d307eed6e9e6cceb21374233512ba08b63a7819d1d76d30e661e658674191351b"

    */
    function verify(
        address _signer,
        IMessageStructure.Message memory _message,
        IMessageStructure.DomainSeparator memory _domain,
        bytes memory signature
    ) internal pure returns (bool) {
        bytes32 messageHash = getMessageHash(_message, _domain);
        bytes32 ethSignedMessageHash = getEthSignedMessageHash(messageHash);

        return recoverSigner(ethSignedMessageHash, signature) == _signer;
    }

    function recoverSigner(
        bytes32 _ethSignedMessageHash,
        bytes memory _signature
    ) internal pure returns (address) {
        (bytes32 r, bytes32 s, uint8 v) = splitSignature(_signature);

        return ecrecover(_ethSignedMessageHash, v, r, s);
    }

    function splitSignature(bytes memory sig)
        internal
        pure
        returns (
            bytes32 r,
            bytes32 s,
            uint8 v
        )
    {
        require(sig.length == 65, "invalid signature length");

        assembly {
            /*
            First 32 bytes stores the length of the signature

            add(sig, 32) = pointer of sig + 32
            effectively, skips first 32 bytes of signature

            mload(p) loads next 32 bytes starting at the memory address p into memory
            */

            // first 32 bytes, after the length prefix
            r := mload(add(sig, 32))
            // second 32 bytes
            s := mload(add(sig, 64))
            // final byte (first byte of the next 32 bytes)
            v := byte(0, mload(add(sig, 96)))
        }

        // implicitly return (r, s, v)
    }
}
